import React from 'react'
import * as BooksAPI from './BooksAPI'
import './App.css'
import MainPage from './MainPage'
import { Route } from 'react-router-dom'
//import SearchPage from './SearchPage';

class BooksApp extends React.Component {
  state = {
    books: []
  }

  componentDidMount() {
    this.getBooks();
  }

  getBooks = () => {
    BooksAPI.getAll().then((result) => {
      this.setState({
        books: result
      });
    });
  }

  onSelectionChanged = (event, book) => {
    const shelf = event.target.value
    BooksAPI.update(book, shelf)

    let isBooksMatched = false;

    let booksWithUpdatedShelves = this.state.books.map((oldBook, index) => {
      if(oldBook.id === book.id) {
        isBooksMatched = true
        book.shelf = shelf
        return book
      } 
      return oldBook
    })

    this.setBooksState(isBooksMatched, booksWithUpdatedShelves, book, shelf);  
  }

  setBooksState = (isBooksMatched, booksWithUpdatedShelves, book, shelf) => {
    if(isBooksMatched) {  
      this.setState(prevState => ({
        books: newBooks,
      }))
    }else {
      book.shelf = shelf
      this.setState(currentState => ({
        books: [...currentState.books, book]
      }))
    }
  }

  render() {
    return (
      <div className="app">
          {/* <Route path='/search' render={() => (
                <SearchPage
                  shelfBooks = {this.state.books}
                  onSelectionChanged={this.onSelectionChanged}
                />
            )} /> */}
          <Route exact path='/'
            render={() => (
              <MainPage
                books={this.state.books}
                onSelectionChanged={this.onSelectionChanged}/> 
            )} />
      </div>
    )
  }
}

export default BooksApp
